INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (156, 1, '2013-12-09 18:29:52', '2013-12-09 10:29:52', 'Pike Place Market is a public market overlooking the Elliott Bay waterfront in Seattle, Washington, United States. The Market opened August 17, 1907, and is one of the oldest continuously operated public farmers\' markets in the United States. It is a place of business for many small farmers, craftspeople and merchants. Named after the central street, Pike Place runs northwest from Pike Street to Virginia Street, and remains one of Seattle\'s most popular tourist destinations.\r\n\r\nThe Market is built on the edge of a steep hill, and consists of several lower levels located below the main level. Each features a variety of unique shops such as antique dealers, comic book and collectible shops, small family-owned restaurants, and one of the oldest head shops in Seattle. The upper street level contains fishmongers, fresh produce stands and craft stalls operating in the covered arcades. Local farmers and craftspeople sell year-round in the arcades from tables they rent from the Market on a daily basis, in accordance with the Market\'s mission and founding goal: allowing consumers to \"Meet the Producer\".', 'Explore Seattle', '', 'inherit', 'open', 'open', '', '66-revision-v1', '', '', '2013-12-09 18:29:52', '2013-12-09 10:29:52', '', 66, 'http://qdgytrans.com/wordpress/?p=156', 0, 'revision', '', 0)#endQuery
INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (154, 1, '2013-12-09 18:29:18', '2013-12-09 10:29:18', '<b>Pike Place Market</b> is a <a title=\"Public market\" href=\"http://en.wikipedia.org/wiki/Public_market\">public market</a> overlooking the <a title=\"Elliott Bay\" href=\"http://en.wikipedia.org/wiki/Elliott_Bay\">Elliott Bay</a> waterfront in <a title=\"Seattle\" href=\"http://en.wikipedia.org/wiki/Seattle\">Seattle</a>, Washington, United States. The Market opened August 17, 1907, and is one of the oldest continuously operated public farmers\' markets in the United States. It is a place of business for many small farmers, <a title=\"Craft\" href=\"http://en.wikipedia.org/wiki/Craft\">craftspeople</a> and <a title=\"Merchant\" href=\"http://en.wikipedia.org/wiki/Merchant\">merchants</a>. Named after the central street, Pike Place runs northwest from Pike Street to Virginia Street, and remains one of Seattle\'s most popular tourist destinations.\r\n\r\nThe Market is built on the edge of a steep hill, and consists of several lower levels located below the main level. Each features a variety of unique <a title=\"Retailing\" href=\"http://en.wikipedia.org/wiki/Retailing#Shops_and_stores\">shops</a> such as <a title=\"Antiques\" href=\"http://en.wikipedia.org/wiki/Antiques\">antique</a> dealers, <a title=\"Comic book\" href=\"http://en.wikipedia.org/wiki/Comic_book\">comic book</a> and collectible shops, small family-owned <a title=\"Restaurant\" href=\"http://en.wikipedia.org/wiki/Restaurant\">restaurants</a>, and one of the oldest <a title=\"Head shop\" href=\"http://en.wikipedia.org/wiki/Head_shop\">head shops</a> in Seattle. The upper street level contains <a title=\"Fishmonger\" href=\"http://en.wikipedia.org/wiki/Fishmonger\">fishmongers</a>, fresh <a title=\"Produce\" href=\"http://en.wikipedia.org/wiki/Produce\">produce</a> stands and craft stalls operating in the covered <a title=\"Arcade (architecture)\" href=\"http://en.wikipedia.org/wiki/Arcade_(architecture)\">arcades</a>. Local farmers and craftspeople sell year-round in the arcades from tables they <a title=\"Renting\" href=\"http://en.wikipedia.org/wiki/Renting\">rent</a> from the Market on a daily basis, in accordance with the Market\'s mission and founding goal: allowing consumers to \"Meet the Producer\".', 'Explore Seattle', '', 'inherit', 'open', 'open', '', '66-revision-v1', '', '', '2013-12-09 18:29:18', '2013-12-09 10:29:18', '', 66, 'http://qdgytrans.com/wordpress/?p=154', 0, 'revision', '', 0)#endQuery
