INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (364, 1, '2014-03-05 17:03:19', '2014-03-05 09:03:19', '\r\n[caption id=\"attachment_369\" align=\"alignleft\" width=\"300\"]<a href=\"http://www.qdgytrans.com/blog/wp-content/uploads/2014/03/IMG_0263.jpg\"><img src=\"http://www.qdgytrans.com/blog/wp-content/uploads/2014/03/IMG_0263-300x208.jpg\" alt=\"早市\" width=\"300\" height=\"208\" class=\"size-medium wp-image-369\" /></a> 中国的早市<br />[/caption]\r\n\r\n在国外呆了4,5年之后发现自己已经渐渐忘却了很多中国的点点滴滴。\r\n\r\n今天早上起来，突发奇想要给自己做早餐。\r\n打开冰箱一看几乎什么材料都没有。\r\n没办法，只能去超市买菜了。\r\n在去超市的路上，我突然悟道日本不像中国，这个国家是没有早市的。\r\n在国内的时候，早上早起去早市买豆浆，油条还有豆腐脑。。。\r\n中国的早市很热闹，新鲜蔬菜，水果，鱼肉应有尽有\r\n几乎每个小区附近都会有一个大大小小的早市\r\n现在想想早市是一个生活的象征，是一天的开始\r\n一大早去早市接触的人，买早餐其实是非常珍贵的也是非常健康的\r\n很多发达国家充满了大大小小的超市，\r\n虽然可以节省时间，但是人们的表情却越来越少了\r\n每天早上都被时间赶着，都没有闲情去享受一天的开始了\r\n\r\n早市，我希望你不要消失，守护中国人的健康生活！\r\n\r\n文章原创：青岛广译翻译事务所', '早市文化', '', 'publish', 'open', 'open', '', 'chinese-culture', '', '', '2014-04-29 23:40:44', '2014-04-29 15:40:44', '', 0, 'http://qdgytrans.com/wordpress/?p=364', 0, 'post', '', 0)#endQuery
INSERT INTO `wp_posts` (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (365, 1, '2014-03-05 17:03:19', '2014-03-05 09:03:19', '中国以美食闻名天下，在国外呆了4,5年之后发现自己已经渐渐忘却了很多中国的点点滴滴。\r\n\r\n今天早上起来，突发奇想要给自己做早餐。\r\n打开冰箱一看几乎什么材料都没有。\r\n没办法，只能去超市买菜了。\r\n在去超市的路上，我突然悟道日本不像中国，这个国家是没有早市的。\r\n在国内的时候，早上早起去早市买豆浆，油条还有豆腐脑。。。\r\n中国的早市很热闹，新鲜蔬菜，水果，鱼肉应有尽有\r\n几乎每个小区附近都会有一个大大小小的早市\r\n现在想想早市是一个生活的象征，是一天的开始\r\n一大早去早市接触的人，买早餐其实是非常珍贵的也是非常健康的\r\n很多发达国家充满了大大小小的超市，\r\n虽然可以节省时间，但是人们的表情却越来越少了\r\n每天早上都被时间赶着，都没有闲情去享受一天的开始了\r\n\r\n早市，我希望你不要消失！\r\n\r\n\r\n', '早市文化', '', 'inherit', 'open', 'open', '', '364-revision-v1', '', '', '2014-03-05 17:03:19', '2014-03-05 09:03:19', '', 364, 'http://qdgytrans.com/wordpress/2014/03/364-revision-v1/', 0, 'revision', '', 0)#endQuery
